script.script:
def TIMEOUT = org.jenkinsci.plugins.gitclient.CliGitAPIImpl.TIMEOUT
def timeOut = Integer.getInteger("org.jenkinsci.plugins.gitclient.Git.timeOut", TIMEOUT)

if (TIMEOUT != timeOut) {
    manager.addWarningBadge("TIMEOUT == " + TIMEOUT + " timeOut == " + timeOut)
    manager.buildUnstable()
}
