definition.script:
pipeline {
    parameters {
        choice choices: ['freebsd', 'linux', 'openbsd'],
            description: 'Label for the agent that will run the command', 
            name: 'AGENT_LABEL'
    }
    agent {
        label params.AGENT_LABEL
    }
    stages {
        stage('Hello') {
            steps {
                sh "echo AGENT_LABEL is ${params.AGENT_LABEL}; echo Hello World from $AGENT_LABEL; uname -a"
            }
        }
    }
}
