definition.script:
def badge = addEmbeddableBadgeConfiguration(id: "my-build", subject: "My Build")

pipeline {
    agent any

    stages {
        stage('Building') {
            steps {
                script {
                    badge.setStatus('my build is building')
                }
                echo 'Hello World'
                sleep 10
            }
        }
        stage('Testing') {
            steps {
                script {
                    badge.setStatus('my build is testing')
                    badge.setColor('blue')
                }
                echo 'Testing World'
                sleep 10
                script {
                    badge.setColor('brightgreen')
                    badge.setStatus('my build is a success')
                }
            }
        }
    }
}
